css_topics.example:
.box {
  margin: 20px;
  border: 2px solid #333;
  padding: 15px;
  width: 300px;
  height: 200px;
}

.container {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
}

.item {
  flex: 1;
}

.grid-container {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}

.grid-item {
  grid-column: span 1;
}

.header {
  position: fixed;
  top: 0;
  width: 100%;
  z-index: 1000;
}

.modal {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

/* Menggunakan nama warna */
body {
  background-color: white;
}

/* Menggunakan hex code */
.header {
  background-color: #007bff;
}

/* Menggunakan rgb */
.card {
  background-color: rgb(255, 255, 255);
}

/* Menggunakan rgba dengan opacity */
.modal {
  background-color: rgba(0, 0, 0, 0.5);
}

/* Menggunakan hsl */
.section {
  background-color: hsl(210, 100%, 50%);
}

/* Type selector */
p {
  color: black;
}

/* Class selector */
.text-red {
  color: red;
}

/* ID selector */
#header {
  background: blue;
}

/* Attribute selector */
input[type="text"] {
  border: 1px solid #ccc;
}

/* Pseudo-class */
a:hover {
  text-decoration: underline;
}

/* Specificity hierarchy: ID > Class > Type */

/* Font properties */
body {
  font-family: 'Arial', sans-serif;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.6;
}

.heading {
  font-size: 2rem;
  font-weight: 700;
  letter-spacing: 0.5px;
}

.italic {
  font-style: italic;
}

.underline {
  text-decoration: underline;
}

/* Text alignment */
.text-center {
  text-align: center;
}

/* Transition */
.button {
  transition: all 0.3s ease-in-out;
}
.button:hover {
  transform: scale(1.05);
  background-color: #007bff;
}

/* Animation dengan keyframes */
@keyframes slideIn {
  from {
    transform: translateX(-100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.slide-in {
  animation: slideIn 0.5s ease-out;
}

/* Animation properties */
.fade-in {
  animation: fadeIn 1s ease-in forwards;
  opacity: 0;
}

javascript_topics.example:
// Deklarasi variabel
let nama = 'John';
const usia = 25;
var alamat = 'Jakarta';

// Re-assignment
nama = 'Doe';
// usia = 26; // Error: const tidak bisa di-reassign

// Aritmatika
let a = 10 + 5; // 15
let b = 10 * 2; // 20

// Perbandingan
console.log(5 == '5');  // true (value)
console.log(5 === '5'); // false (value + type)

// Ternary
let status = (usia >= 17) ? 'Dewasa' : 'Anak';

// For loop
for (let i = 0; i < 5; i++) {
  console.log(i);
}

// If condition
if (usia >= 18) {
  console.log('Dewasa');
} else {
  console.log('Anak');
}

// Switch case
switch (hari) {
  case 'Senin':
    console.log('Start week');
    break;
  default:
    console.log('Other day');
}

// Concatenation dengan +
let namaLengkap = namaDepan + ' ' + namaBelakang;

// Template Literals (ES6)
let greeting = `Halo, ${namaDepan} ${namaBelakang}!`;

// Multi-line string
let alamat = `Jalan Merdeka No. 123
Kota Jakarta Selatan`;

// Function declaration
function sapa(nama) {
  return `Halo ${nama}!`;
}

// Arrow function (ES6)
const tambah = (a, b) => a + b;

// Function expression
const kali = function(a, b) {
  return a * b;
};

// Object literal
let person = {
  nama: 'John Doe',
  usia: 25,
  sapa: function() {
    return `Halo, saya ${this.nama}`;
  }
};

// Access properties
console.log(person.nama); // John Doe
console.log(person.sapa()); // Halo, saya John Doe

// Create new Date
let sekarang = new Date();

// Get methods
console.log(sekarang.getFullYear()); // 2025
console.log(sekarang.getMonth());    // 9 (Oktober)
console.log(sekarang.getDate());     // 18
console.log(sekarang.getTime());     // timestamp

// Format date
let formatted = sekarang.toLocaleDateString('id-ID');

// Get element by ID
let header = document.getElementById('header');

// Get elements by class
let items = document.getElementsByClassName('item');

// Query selector
let button = document.querySelector('.btn-primary');

// Modify content
header.textContent = 'New Title';
button.style.backgroundColor = 'blue';

// Inline HTML
<button onclick="handleClick()">Click Me</button>

// JavaScript event listener
let btn = document.getElementById('myBtn');
btn.addEventListener('click', function() {
  alert('Button clicked!');
});

// Event object
btn.addEventListener('click', function(event) {
  console.log(event.target); // element yang diklik
});

// HTML onclick attribute
<button onclick="alert('Clicked!')">Click</button>

// JavaScript onclick property
let btn = document.getElementById('btn');
btn.onclick = function() {
  console.log('Button clicked');
};

// Multiple handlers dengan addEventListener
btn.addEventListener('click', handler1);
btn.addEventListener('click', handler2);

let numbers = [1, 2, 3, 4, 5];

// Add/remove
numbers.push(6);       // [1,2,3,4,5,6]
numbers.pop();         // [1,2,3,4,5]

// Transformation
let doubled = numbers.map(n => n * 2); // [2,4,6,8,10]
let filtered = numbers.filter(n => n > 3); // [4,5]

// Iteration
numbers.forEach(n => console.log(n));

let element = document.getElementById('myElement');

// Navigasi
let parent = element.parentNode;
let firstChild = element.firstChild;
let children = element.children;
let nextSibling = element.nextSibling;

// Query navigasi
let specificChild = element.querySelector('.specific');
let allChildren = element.querySelectorAll('div');

tailwind_topics.example:
// Install via npm
npm install -D tailwindcss

// Initialize config file
npx tailwindcss init

// Add to CSS file
@tailwind base;
@tailwind components;
@tailwind utilities;

// Text styling
text-xl font-bold text-gray-800

// Background & padding
bg-blue-500 p-4

// Margin & border
m-4 border border-2 border-gray-300

// Flexbox
flex justify-center items-center

// Mobile first approach
class="text-base md:text-lg lg:text-xl"

// Grid responsive
grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3

// Hide/show on breakpoint
hidden md:block

// Flexbox
flex flex-col md:flex-row justify-between items-center

// Grid
grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4

// Grid template columns
grid-cols-4 md:grid-cols-6 lg:grid-cols-8

// Hover effects
hover:bg-blue-600 hover:text-white

// Focus states
focus:outline-none focus:ring-2 focus:ring-blue-500

// Transition
transition duration-300 ease-in-out

// tailwind.config.js
module.exports = {
  content: ['./src/**/*.{html,js}'],
  theme: {
    extend: {
      colors: {
        'company-blue': '#1e40af',
      },
      spacing: {
        '128': '32rem',
      }
    }
  }
}

// Enable dark mode in config
darkMode: 'class',

// Usage
bg-white dark:bg-gray-800
text-gray-900 dark:text-white

// Design tokens dalam config
colors: {
  primary: '#2563eb',
  secondary: '#64748b',
  success: '#22c55e',
  danger: '#ef4444'
},

// Component classes
.btn-primary {
  @apply bg-primary text-white px-4 py-2 rounded;
}

/* Background color dengan kelas Tailwind */
class="bg-white"        // white background
class="bg-blue-500"     // blue background (500 shade)
class="bg-gray-100"     // light gray background
class="bg-red-600"      // red background
class="bg-green-400"    // green background

/* Background dengan opacity */
class="bg-blue-500/50"  // blue dengan 50% opacity
class="bg-red-600/25"   // red dengan 25% opacity

/* Hover states */
class="bg-blue-500 hover:bg-blue-600"  // darker blue on hover

/* Dark mode support */
class="bg-white dark:bg-gray-800"      // white in light mode, dark gray in dark mode
